sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
var concerts = inputs.cons.concerts
gs.info(concerts);
  for(var i = 0; i <= concerts.length ; i ++)
  {
    var gr_concert = new GlideRecord("x_553251_concert_0_concert");
    gr_concert.addQuery("title", concerts[i].name);
    gr_concert.addQuery("link", concerts[i].link);
    gr_concert.query();
    if(gr_concert.next())
    {
    gr_concert.title = concerts[i].name;
    gr_concert.date = concerts[i].date;
    gr_concert.calendar_date_time = concerts[i].cal_date;
    gr_concert.image = concerts[i].image;
    gr_concert.link = concerts[i].link;
    gr_concert.time = concerts[i].time;
    gr_concert.venue = concerts[i].venue;
    gr_concert.update();
    }
    else {
    gr_concert.initialize();
    gr_concert.title = concerts[i].name;
    gr_concert.calendar_date_time = concerts[i].cal_date;
    gr_concert.date = concerts[i].date;
    gr_concert.image = concerts[i].image;
    gr_concert.link = concerts[i].link;
    gr_concert.time = concerts[i].time;
    gr_concert.venue = concerts[i].venue;
    gr_concert.insert();
    }

  }
})(inputs, outputs);

